import React, { useState } from 'react';
import ChatPage from './pages/ChatPage';
import './App.css';

function App() {
  const [userCount, setUserCount] = useState(0); // 실제로는 서버에서 이 값을 받아와야 합니다

  return (
    <div className="App">
      <main className="App-main">
        <div className="content-area">
          {/* 여기에 메인 콘텐츠가 들어갑니다 */}
        </div>
        <div className="side-section">
          <header className="App-header">
            <div className="auth-buttons">
              <button>로그인</button>
              <button>회원가입</button>
            </div>
          </header>
          <ChatPage userCount={userCount} />
        </div>
      </main>
    </div>
  );
}

export default App;
