export const testUsers = [
    { id: 1, username: "user1", password: "password1", nickname: "Nick1", email: "user1@example.com" },
    { id: 2, username: "user2", password: "password2", nickname: "Nick2", email: "user2@example.com" },
    { id: 3, username: "user3", password: "password3", nickname: "Nick3", email: "user3@example.com" }
  ];

  export const findUser = (username, password) => {
    return testUsers.find(user => user.username === username && user.password === password);
  };

  export const addUser = (newUser) => {
    const id = testUsers.length + 1;
    testUsers.push({ ...newUser, id });
    return { ...newUser, id };
  };
