import React, { useRef } from 'react';
import ChatPage from './pages/ChatPage';
import './App.css';

function App() {
  const userCount = useRef(0); // 임시로 0으로 설정, 실제로는 서버에서 이 값을 받아와야 합니다

  return (
    <div className="App">
      <header className="App-header">
        <div className="auth-buttons">
          <div className="user-count">현재 접속자 수: {userCount.current}</div>
          <button>로그인</button>
          <button>회원가입</button>
        </div>
      </header>
      <main className="App-main">
        <div className="content-area">
          {/* 여기에 메인 콘텐츠가 들어갑니다 */}
        </div>
        <ChatPage />
      </main>
    </div>
  );
}

export default App;
