import React, { useState } from 'react';
import { ThemeProvider, useTheme } from './contexts/ThemeContext';
import ChatPage from './pages/ChatPage';
import AuthModal from './components/AuthModal';
import { findUser, addUser } from './utils/testUsers';
import './App.css';

function AppContent() {
  const [userCount, setUserCount] = useState(0);
  const [showAuthModal, setShowAuthModal] = useState(false);
  const [authType, setAuthType] = useState(null);
  const { isDarkMode, toggleTheme } = useTheme();
  const [currentUser, setCurrentUser] = useState(null);

  const handleAuthButton = (type) => {
    setAuthType(type);
    setShowAuthModal(true);
  };

  const handleCloseModal = () => {
    setShowAuthModal(false);
    setAuthType(null);
  };

  const handleLogin = (username, password) => {
    const user = findUser(username, password);
    if (user) {
      setCurrentUser(user);
      handleCloseModal();
      // 여기서 로그인 성공 메시지를 표시할 수 있습니다.
    } else {
      // 로그인 실패 메시지를 표시할 수 있습니다.
    }
  };

  const handleRegister = (newUser) => {
    const user = addUser(newUser);
    setCurrentUser(user);
    handleCloseModal();
    // 여기서 회원가입 성공 메시지를 표시할 수 있습니다.
  };

  const handleLogout = () => {
    setCurrentUser(null);
    // 로그아웃 메시지를 표시할 수 있습니다.
  };

  return (
    <div className={`App ${isDarkMode ? 'dark-mode' : 'light-mode'}`}>
      <div className="main-section">
        <header className="main-header">
          <h1>채팅 애플리케이션</h1>
        </header>
        <main className="main-content">
          {/* 여기에 메인 콘텐츠가 들어갑니다 */}
        </main>
      </div>
      <aside className="side-container">
        <header className="side-header">
          <div className="user-count">현재 접속자 수: {userCount}</div>
          <div className="header-buttons">
            <button onClick={toggleTheme} className="theme-toggle">
              {isDarkMode ? '라이트 모드' : '다크 모드'}
            </button>
            {currentUser ? (
              <>
                <span>Welcome, {currentUser.nickname}!</span>
                <button onClick={handleLogout}>로그아웃</button>
              </>
            ) : (
              <>
                <button onClick={() => handleAuthButton('login')}>로그인</button>
                <button onClick={() => handleAuthButton('register')}>회원가입</button>
              </>
            )}
          </div>
        </header>
        <ChatPage currentUser={currentUser} />
      </aside>
      {showAuthModal && (
        <AuthModal 
          type={authType} 
          onClose={handleCloseModal}
          onLogin={handleLogin}
          onRegister={handleRegister}
        />
      )}
    </div>
  );
}

function App() {
  return (
    <ThemeProvider>
      <AppContent />
    </ThemeProvider>
  );
}

export default App;
